// src/pages/MediaDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getMediaDetails } from '../services/api';
import {
  Container,
  Box,
  Typography,
  Button,
  CircularProgress,
  CardMedia,
  Alert
} from '@mui/material';

const MediaDetail = () => {
  const { type, id } = useParams();
  const navigate = useNavigate();
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    setLoading(true);
    getMediaDetails(type, id)
      .then(res => {
        setMedia(res.data);
        setError('');
      })
      .catch(() => {
        setError('Failed to load media details. Please try again.');
      })
      .finally(() => {
        setLoading(false);
      });
  }, [type, id]);

  // New download handler
  const handleDownload = async () => {
    try {
      const response = await fetch(media.url, { mode: 'cors' });
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      // Use title or fallback to id + extension
      const filename = media.title
        ? media.title.replace(/\s+/g, '_') + media.url.match(/\.\w+$/)?.[0] || ''
        : `${id}${media.url.match(/\.\w+$/)?.[0] || ''}`;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Download error', err);
      setError('Download failed. Please try again.');
    }
  };

  if (loading) {
    return (
      <Container sx={{ py: 4, textAlign: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container sx={{ py: 4 }}>
        <Alert severity="error">{error}</Alert>
        <Box mt={2}>
          <Button variant="outlined" onClick={() => navigate(-1)}>
            &larr; Back
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container sx={{ py: 4 }}>
      <Box mb={3}>
        <Button variant="outlined" onClick={() => navigate(-1)}>
          &larr; Back to results
        </Button>
      </Box>

      <Typography variant="h4" gutterBottom>
        {media.title || 'Untitled'}
      </Typography>
      {media.creator_display_name && (
        <Typography variant="subtitle2" color="text.secondary" gutterBottom>
          By {media.creator_display_name}
        </Typography>
      )}

      <CardMedia
        component={type === 'audio' ? 'audio' : type === 'video' ? 'video' : 'img'}
        src={media.url}
        controls={type !== 'images'}
        height={type === 'images' ? 400 : undefined}
        alt={media.title}
        sx={{ width: '100%', maxHeight: 600, objectFit: 'contain', mb: 2 }}
      />

      <Box display="flex" gap={2} mb={2}>
        <Button
          variant="contained"
          onClick={handleDownload}
        >
          Download
        </Button>
      </Box>

      {media.description && (
        <Typography paragraph>{media.description}</Typography>
      )}
    </Container>
  );
};

export default MediaDetail;
