// src/components/ThreeBackground.js
import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';

// Simple animated sphere that won't cause errors
const AnimatedSphere = ({ position, color }) => {
  return (
    <mesh position={position}>
      <sphereGeometry args={[1, 32, 32]} />
      <meshStandardMaterial color={color} />
    </mesh>
  );
};

// Simplified background component
const ThreeBackground = ({ style }) => {
  return (
    <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', ...style }}>
      <Canvas camera={{ position: [0, 0, 5], fov: 75 }}>
        <ambientLight intensity={0.7} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <pointLight position={[-10, -10, -10]} intensity={0.5} color="#e1c5ff" />
        
        <AnimatedSphere position={[-3, 0, 0]} color="#5e8bff" />
        <AnimatedSphere position={[3, 0, -2]} color="#5ad4a6" />
        <AnimatedSphere position={[0, 2, -5]} color="#ffb470" />
        
        <OrbitControls enableZoom={false} enablePan={false} />
      </Canvas>
    </div>
  );
};

export default ThreeBackground;
