// src/components/MediaGrid.js
import React from 'react';
import {
  Box,
  Typography,
  ImageList,
  ImageListItem,
  ImageListItemBar,
  Skeleton,
  Button,
  useTheme,
  useMediaQuery
} from '@mui/material';

const MediaGrid = ({
  items = [],
  loading = false,
  onItemClick = () => {},
  onLoadMore = null,
  cols = { xs: 1, sm: 2, md: 3 }
}) => {
  const theme = useTheme();
  const isSmUp = useMediaQuery(theme.breakpoints.up('sm'));
  const isMdUp = useMediaQuery(theme.breakpoints.up('md'));
  const numCols = isMdUp ? cols.md : isSmUp ? cols.sm : cols.xs;

  return (
    <Box sx={{ py: 4, px: 2 }}>
      <Typography variant="h4" gutterBottom>
        Trending Media
      </Typography>
      <Typography variant="body1" color="text.secondary" gutterBottom>
        Explore popular and trending creative assets
      </Typography>

      {loading ? (
        <ImageList variant="masonry" cols={numCols} gap={8}>
          {Array.from({ length: 6 }).map((_, idx) => (
            <ImageListItem key={idx}>
              <Skeleton
                variant="rectangular"
                width="100%"
                height={180}
                animation="wave"
              />
              <Box sx={{ pt: 1 }}>
                <Skeleton width="60%" />
                <Skeleton width="40%" />
              </Box>
            </ImageListItem>
          ))}
        </ImageList>
      ) : (
        <ImageList variant="masonry" cols={numCols} gap={8}>
          {items.map(item => (
            <ImageListItem
              key={item.id}
              sx={{ cursor: 'pointer', '&:hover img': { opacity: 0.8 } }}
              onClick={() => onItemClick(item)}
            >
              <img
                src={item.thumbnail || item.imageUrl}
                alt={item.title}
                loading="lazy"
                style={{
                  borderRadius: theme.shape.borderRadius,
                  width: '100%'
                }}
              />
              <ImageListItemBar
                title={item.title}
                subtitle={item.creator_display_name || item.type}
                position="below"
              />
            </ImageListItem>
          ))}
        </ImageList>
      )}

      {onLoadMore && !loading && (
        <Box textAlign="center" mt={3}>
          <Button variant="contained" onClick={onLoadMore}>
            Load More
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default React.memo(MediaGrid);
