// src/pages/Favorites.js
import React, { useEffect, useState } from 'react';
import { getFavorites, removeFavorite } from '../services/api';
import {
  Container, Grid, Card, CardMedia, CardContent, Typography,
  IconButton, Box, CircularProgress, Alert
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState('');

  const fetchFavorites = async () => {
    try {
      const { data } = await getFavorites();
      setFavorites(data);
    } catch {
      setError('Failed to load favorites.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFavorites();
  }, []);

  const handleRemove = async (id) => {
    try {
      await removeFavorite(id);
      setFavorites(favs => favs.filter(f => f.id !== id));
    } catch {
      setError('Could not remove favorite.');
    }
  };

  if (loading) return <Box textAlign="center"><CircularProgress/></Box>;
  if (error)   return <Alert severity="error">{error}</Alert>;

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>My Favorites</Typography>
      {favorites.length === 0
        ? <Typography>No favorites yet.</Typography>
        : (
          <Grid container spacing={3}>
            {favorites.map(fav => (
              <Grid item key={fav.id} xs={12} sm={6} md={4}>
                <Card>
                  <CardMedia
                    component="img"
                    height="180"
                    image={fav.thumbnail_url}
                    alt={fav.title}
                  />
                  <CardContent sx={{ position: 'relative' }}>
                    <Typography variant="subtitle1" noWrap>
                      {fav.title}
                    </Typography>
                    <IconButton
                      onClick={() => handleRemove(fav.id)}
                      sx={{ position: 'absolute', top: 8, right: 8 }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )
      }
    </Container>
  );
};

export default Favorites;
