// src/components/ThreeBackground.js
import React from 'react';
import { keyframes, styled } from '@mui/system';

// Define a smooth gradient–shift animation
const gradientShift = keyframes`
  0%   { background-position:   0% 50%; }
  50%  { background-position 100% 50%; }
  100% { background-position   0% 50%; }
`;

// Styled full-screen div with animated gradient
const AnimatedBackground = styled('div')({
  position: 'fixed',
  inset: 0,
  zIndex: -1,
  background: 'linear-gradient(135deg, #323259 0%, #5e8bff 100%)',
  backgroundSize: '200% 200%',
  animation: `${gradientShift} 20s ease infinite`
});

const ThreeBackground = ({ style }) => (
  <AnimatedBackground style={style} />
);

export default ThreeBackground;
