// src/pages/Home.js
import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { useLocation, useNavigate, Link as RouterLink } from 'react-router-dom';
import debounce from 'lodash/debounce';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  Button,
  Container,
  Chip,
  Switch,
  FormControlLabel,
  Backdrop,
  Fade,
  CircularProgress
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import MediaGrid from '../components/MediaGrid';
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Art', 'Nature', 'Technology', 'Architecture'];
const PER_PAGE = 20;

const Home = ({ user }) => {
  const location = useLocation();
  const navigate  = useNavigate();

  const [darkMode, setDarkMode] = useState(false);
  const [query, setQuery]     = useState('');
  const [results, setResults] = useState([]);
  const [page, setPage]       = useState(1);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);
  const [error, setError]     = useState('');

  const theme = useMemo(
    () =>
      createTheme({
        palette: { mode: darkMode ? 'dark' : 'light', primary: { main: '#009688' } }
      }),
    [darkMode]
  );

  // Enhanced search with pagination and append flag
  const runSearch = useCallback(
    async (term, pageNum = 1, append = false) => {
      const q = (term !== undefined ? term : query).trim();
      if (!q) {
        setError('Please enter a search term.');
        return;
      }
      if (q.length < 3) {
        setError('Please enter at least 3 characters.');
        return;
      }
      setError('');
      setLoading(true);
      setSearched(true);
      try {
        const { data } = await searchMedia(q, 'images', pageNum, PER_PAGE);
        const newItems = data.results || [];
        setResults(current => append ? [...current, ...newItems] : newItems);
        setPage(pageNum);
      } catch {
        setError('Network error, please try again.');
        if (!append) setResults([]); 
      } finally {
        setLoading(false);
      }
    },
    [query]
  );

  // Debounced live‐typing search
  const debouncedSearch = useMemo(() => debounce(runSearch, 300), [runSearch]);
  useEffect(() => () => debouncedSearch.cancel(), [debouncedSearch]);

  // Auto-run on ?q= in URL
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const q = params.get('q');
    if (q && q.trim().length >= 3) {
      setQuery(q);
      runSearch(q, 1, false);
    }
  }, [location.search, runSearch]);

  const handleChange = e => {
    const val = e.target.value;
    setQuery(val);
    debouncedSearch(val, 1, false);
  };

  const handleSubmit = e => {
    e.preventDefault();
    debouncedSearch.cancel();
    runSearch(query, 1, false);
  };

  const handleLoadMore = () => {
    runSearch(query, page + 1, true);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground />

      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
            LincolnVerse
          </Typography>
          <FormControlLabel
            control={
              <Switch
                checked={darkMode}
                onChange={() => setDarkMode(prev => !prev)}
                color="default"
              />
            }
            label="Dark Mode"
            sx={{ color: 'inherit' }}
          />
          {user && <Typography sx={{ ml: 2 }}>Hi, {user.username}</Typography>}
        </Toolbar>
      </AppBar>

      <Fade in timeout={600}>
        <Box sx={{ pt: 8, pb: 4, textAlign: 'center', bgcolor: 'primary.main', color: '#fff' }}>
          <Container maxWidth="md">
            <Typography variant="h3" gutterBottom>
              Discover Open-Licensed Media
            </Typography>
            <Box
              component="form"
              onSubmit={handleSubmit}
              sx={{
                display: 'flex',
                justifyContent: 'center',
                gap: 2,
                mt: 2,
                flexWrap: 'wrap'
              }}
            >
              <TextField
                variant="filled"
                placeholder="Search media..."
                value={query}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <Box component="span" sx={{ pl: 1, display: 'flex', alignItems: 'center' }}>
                      <SearchIcon />
                    </Box>
                  )
                }}
                sx={{ bgcolor: '#E0F2F1', borderRadius: 1, width: { xs: '100%', md: '50%' } }}
              />
              <Button
                type="submit"
                variant="contained"
                sx={{ py: 1.5, px: 4, bgcolor: '#004D40' }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Search'}
              </Button>
            </Box>

            {!searched && (
              <Box sx={{ mt: 4 }}>
                {trendingTopics.map(topic => (
                  <Chip
                    key={topic}
                    label={topic}
                    onClick={() => runSearch(topic, 1, false)}
                    clickable
                    sx={{ m: 0.5, bgcolor: '#B2DFDB', color: '#004D40' }}
                  />
                ))}
              </Box>
            )}
          </Container>
        </Box>
      </Fade>

      <Container sx={{ py: 4 }}>
        {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}

        <MediaGrid
          items={results}
          loading={loading}
          onItemClick={item =>
            navigate(`/media/${item.asset_type || 'images'}/${item.id}`)
          }
          onLoadMore={searched && results.length === page * PER_PAGE ? handleLoadMore : null}
        />

        {!searched && (
          <Box textAlign="center" mt={4}>
            <Button variant="outlined" onClick={() => runSearch('', 1, false)}>
              Explore Trending
            </Button>
          </Box>
        )}
      </Container>

      <Box sx={{ py: 2, textAlign: 'center', bgcolor: 'grey.100' }}>
        <Typography variant="body2">LincolnVerse • Noel Adesola • 2025</Typography>
      </Box>
    </ThemeProvider>
  );
};

export default Home;
