// src/pages/ForgotPassword.js
import React, { useState, useMemo } from 'react';
import { sendResetLink } from '../services/api';
import { Link as RouterLink } from 'react-router-dom';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  Container,
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  FormControlLabel,
  Switch,
  Backdrop,
  Fade,
  CircularProgress,
  Alert,
  InputAdornment,
  Link as MuiLink
} from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import ThreeBackground from '../components/ThreeBackground';

export default function ForgotPassword() {
  const [darkMode, setDarkMode] = useState(false);
  const theme = useMemo(
    () => createTheme({ palette: { mode: darkMode ? 'dark' : 'light', primary: { main: '#009688' } } }),
    [darkMode]
  );

  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!email.trim()) {
      setError('Please enter your email address.');
      return;
    }
    setLoading(true);
    try {
      await sendResetLink({ email });
      setSuccess('A password reset link has been sent to your email.');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send reset link. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="sm">
        {/* Dark mode toggle */}
        <Box mt={2} display="flex" justifyContent="flex-end">
          <FormControlLabel
            control={<Switch checked={darkMode} onChange={() => setDarkMode(prev => !prev)} color="default" />}
            label="Dark Mode"
            sx={{ color: 'text.primary' }}
          />
        </Box>
        <Fade in timeout={600}>
          <Box display="flex" flexDirection="column" alignItems="center" mt={4}>
            <Paper elevation={3} sx={{ p: 4, width: '100%', borderTop: 4, borderColor: 'primary.main', position: 'relative' }}>
              {loading && (
                <Backdrop open sx={{ position: 'absolute', color: '#fff', zIndex: theme.zIndex.drawer + 1 }}>
                  <CircularProgress />
                </Backdrop>
              )}
              <Box mb={2} textAlign="center">
                <Typography variant="h5" gutterBottom>Forgot Password</Typography>
                <Typography variant="body2" color="text.secondary">
                  Enter your email to receive a password reset link
                </Typography>
              </Box>
              {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
              {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
              <Box component="form" onSubmit={handleSubmit} noValidate>
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  type="email"
                  label="Email Address"
                  autoComplete="email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  InputProps={{ startAdornment: <InputAdornment position="start"><EmailIcon /></InputAdornment> }}
                />
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  sx={{ mt: 2, mb: 1, py: 1.5 }}
                  disabled={loading}
                >
                  {loading ? 'Sending...' : 'Send Reset Link'}
                </Button>
                <Box textAlign="center" mt={1}>
                  <Typography variant="body2">
                    Remembered your password?{' '}
                    <MuiLink component={RouterLink} to="/login" sx={{ fontWeight: 'bold', color: theme.palette.primary.main }}>
                      Sign In
                    </MuiLink>
                  </Typography>
                </Box>
              </Box>
            </Paper>
          </Box>
        </Fade>
      </Container>
      {/* Background visible on larger screens */}
      <Box
        sx={{
          display: { xs: 'none', md: 'block' },
          position: 'fixed',
          inset: 0,
          zIndex: -1,
          opacity: 0.1
        }}
      >
        <ThreeBackground />
      </Box>
    </ThemeProvider>
  );
}
