// src/pages/Home.js
import React, { useState, useMemo, useCallback, useEffect } from 'react';
import debounce from 'lodash/debounce';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  InputAdornment,
  Chip,
  Switch,
  FormControlLabel,
  Backdrop,
  Fade,
  CircularProgress,
  Skeleton
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Art', 'Nature', 'Technology', 'Architecture'];

export default function Home({ user }) {
  // Theme state
  const [darkMode, setDarkMode] = useState(false);
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? 'dark' : 'light',
          primary: { main: '#009688' }
        }
      }),
    [darkMode]
  );

  // Search state
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);
  const [error, setError] = useState('');

  // Perform search
  const runSearch = useCallback(async term => {
    const q = term ?? query;
    if (!q.trim()) {
      setError('Please enter a search term.');
      return;
    }
    if (q.trim().length < 3) {
      setError('Please enter at least 3 characters.');
      return;
    }
    setError('');
    setLoading(true);
    setSearched(true);
    try {
      const { results: items } = await searchMedia(q);
      setResults(items || []);
    } catch {
      setError('Network error, please try again.');
      setResults([]);
    } finally {
      setLoading(false);
    }
  }, [query]);

  // Debounce
  const debounced = useMemo(() => debounce(runSearch, 300), [runSearch]);
  useEffect(() => () => debounced.cancel(), [debounced]);

  const handleChange = e => {
    setQuery(e.target.value);
    debounced(e.target.value);
  };
  const handleSubmit = e => {
    e.preventDefault();
    debounced.cancel();
    runSearch();
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.2 }} />

      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
            LincolnVerse
          </Typography>
          {user && <Typography sx={{ mr: 2 }}>Hi, {user.username}</Typography>}
          <FormControlLabel
            control={<Switch checked={darkMode} onChange={() => setDarkMode(prev => !prev)} color="default" />}
            label="Dark Mode"
            sx={{ color: 'inherit' }}
          />
        </Toolbar>
      </AppBar>

      <Fade in timeout={600}>
        <Box sx={{ py: 8, textAlign: 'center', bgcolor: theme.palette.primary.dark, color: '#fff' }}>
          <Container maxWidth="md">
            <Typography variant="h3" gutterBottom>
              Discover Open-Licensed Media
            </Typography>
            <Typography variant="h6" sx={{ mb: 3 }}>
              Search the world’s creative commons assets
            </Typography>
            <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', justifyContent: 'center', gap: 2, flexWrap: 'wrap' }}>
              <TextField
                variant="filled"
                placeholder="Search media..."
                value={query}
                onChange={handleChange}
                InputProps={{
                  startAdornment: <InputAdornment position="start"><SearchIcon /></InputAdornment>
                }}
                sx={{ bgcolor: theme.palette.primary.light, borderRadius: 1, width: { xs: '100%', md: '50%' } }}
                aria-label="Search media"
              />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ py: 1.5, px: 4 }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Search'}
              </Button>
            </Box>

            {!searched && (
              <Box sx={{ mt: 4 }}>
                <Typography variant="h6" sx={{ mb: 1 }}>Trending Now</Typography>
                {trendingTopics.map(topic => (
                  <Chip
                    key={topic}
                    label={topic}
                    onClick={() => runSearch(topic)}
                    clickable
                    sx={{ m: 0.5, bgcolor: '#B2DFDB', color: '#004D40' }}
                    aria-label={`Trending topic ${topic}`}
                  />
                ))}
              </Box>
            )}
          </Container>
        </Box>
      </Fade>

      <Container sx={{ py: 4 }}>
        {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}
        {searched && <Typography variant="h5" gutterBottom>
          {loading ? 'Searching...' : results.length ? `Found ${results.length} items` : 'No results found'}
        </Typography>}

        {loading ? (
          <Backdrop open sx={{ color: '#fff', zIndex: theme.zIndex.drawer + 1 }}>
            <CircularProgress color="inherit" />
          </Backdrop>
        ) : (
          <Grid container spacing={3}>
            {results.map(item => (
              <Grid item key={item.id} xs={12} sm={6} md={4}>
                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <CardActionArea component="a" href={item.url} target="_blank" tabIndex={0}>
                    <CardMedia
                      component="img"
                      height="180"
                      image={item.thumbnail || item.url}
                      alt={item.title || 'Untitled'}
                      loading="lazy"
                    />
                    <CardContent>
                      <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>{item.title || 'Untitled'}</Typography>
                      <Typography variant="body2" color="text.secondary">{item.creator_display_name || 'Unknown'}</Typography>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}

        {!searched && (
          <Box textAlign="center" mt={4}>
            <Button variant="outlined" color="primary" onClick={() => runSearch('')}>
              Explore Trending
            </Button>
          </Box>
        )}
      </Container>

      <Box sx={{ py: 2, textAlign: 'center', bgcolor: theme.palette.background.default }}>
        <Typography variant="body2">LincolnVerse • Noel Adesola • 2025</Typography>
      </Box>
    </ThemeProvider>
  );
}
