// src/pages/Home.js
import { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Grid, 
  Card, 
  CardMedia, 
  CardContent, 
  CardActions,
  Chip,
  IconButton,
  InputAdornment,
  Divider,
  Paper,
  List, 
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  CircularProgress,
  useTheme,
  MenuItem,
  Select,
  FormControl,
  InputLabel
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import DeleteIcon from '@mui/icons-material/Delete';
import ImageIcon from '@mui/icons-material/Image';
import HistoryIcon from '@mui/icons-material/History';
import { searchMedia, saveSearch, getRecentSearches, deleteSearch } from '../services/api';

const Home = ({ user }) => {
    const theme = useTheme();
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [recentSearches, setRecentSearches] = useState([]);
    const [loading, setLoading] = useState(false);
    const [mediaType, setMediaType] = useState('images');
    const [error, setError] = useState('');

    useEffect(() => {
        if (user) {
            getRecentSearches().then(res => setRecentSearches(res.data));
        }
    }, [user]);

    const handleSearch = async () => {
        if (!query.trim()) {
            setError('Please enter a search term');
            return;
        }
        
        setError('');
        setLoading(true);
        try {
            const res = await searchMedia(query, mediaType);
            setResults(res.data.results);
            if (user) await saveSearch(query);
            // Refresh recent searches
            if (user) {
                const searchesRes = await getRecentSearches();
                setRecentSearches(searchesRes.data);
            }
        } catch (err) {
            console.error('Search error:', err);
            setError('Failed to fetch results. Please try again.');
            setResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteSearch = async (id) => {
        try {
            await deleteSearch(id);
            setRecentSearches(recentSearches.filter(search => search.id !== id));
        } catch (err) {
            console.error('Error deleting search:', err);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    return (
        <Box>
            {/* Hero Section */}
            <Paper
                elevation={0}
                sx={{
                    bgcolor: 'black',
                    color: 'primary.main',
                    p: { xs: 4, md: 6 },
                    mb: 4,
                    borderRadius: 2,
                    backgroundImage: 'linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8))',
                    position: 'relative',
                }}
            >
                <Typography 
                    variant="h3" 
                    component="h1" 
                    fontWeight="bold"
                    sx={{ mb: 2 }}
                >
                    Discover Open Licensed Media
                </Typography>
                <Typography variant="h6" sx={{ mb: 4, maxWidth: 700 }}>
                    Search millions of free-to-use images, sounds, and more from the Openverse API
                </Typography>

                <Box 
                    sx={{ 
                        display: 'flex', 
                        flexDirection: { xs: 'column', sm: 'row' },
                        gap: 2,
                        maxWidth: 700
                    }}
                >
                    <TextField
                        fullWidth
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Search for media..."
                        variant="outlined"
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <SearchIcon />
                                </InputAdornment>
                            ),
                            sx: { 
                                bgcolor: 'white',
                                borderRadius: 1
                            }
                        }}
                    />
                    
                    <FormControl sx={{ minWidth: 120 }}>
                        <InputLabel id="media-type-label">Media Type</InputLabel>
                        <Select
                            labelId="media-type-label"
                            value={mediaType}
                            onChange={(e) => setMediaType(e.target.value)}
                            label="Media Type"
                            sx={{ bgcolor: 'white', height: '56px' }}
                        >
                            <MenuItem value="images">Images</MenuItem>
                            <MenuItem value="audio">Audio</MenuItem>
                        </Select>
                    </FormControl>
                    
                    <Button
                        variant="contained"
                        size="large"
                        onClick={handleSearch}
                        disabled={loading}
                        sx={{ 
                            px: 4, 
                            height: '56px'
                        }}
                    >
                        {loading ? <CircularProgress size={24} /> : 'Search'}
                    </Button>
                </Box>
                
                {error && (
                    <Typography color="error" sx={{ mt: 2 }}>
                        {error}
                    </Typography>
                )}
            </Paper>

            {/* Main Content */}
            <Grid container spacing={4}>
                {/* Results Section */}
                <Grid item xs={12} md={user ? 8 : 12}>
                    <Typography variant="h5" component="h2" fontWeight="bold" sx={{ mb: 3, display: 'flex', alignItems: 'center' }}>
                        <ImageIcon sx={{ mr: 1 }} />
                        Search Results
                    </Typography>

                    {loading ? (
                        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
                            <CircularProgress />
                        </Box>
                    ) : results.length > 0 ? (
                        <Grid container spacing={2}>
                            {results.map((media) => (
                                <Grid item key={media.id} xs={12} sm={6} md={4} lg={3}>
                                    <Card 
                                        sx={{ 
                                            height: '100%', 
                                            display: 'flex', 
                                            flexDirection: 'column',
                                            transition: 'transform 0.2s',
                                            '&:hover': {
                                                transform: 'translateY(-4px)',
                                                boxShadow: '0 12px 20px rgba(0, 0, 0, 0.1)'
                                            }
                                        }}
                                    >
                                        <CardMedia
                                            component="img"
                                            height="140"
                                            image={media.thumbnail || 'https://via.placeholder.com/150'}
                                            alt={media.title}
                                            sx={{ objectFit: 'cover' }}
                                        />
                                        <CardContent sx={{ flexGrow: 1 }}>
                                            <Typography variant="subtitle1" component="div" noWrap>
                                                {media.title}
                                            </Typography>
                                            <Typography variant="body2" color="text.secondary">
                                                {media.creator ? `By: ${media.creator}` : 'Unknown creator'}
                                            </Typography>
                                            {media.license && (
                                                <Chip 
                                                    label={media.license} 
                                                    size="small" 
                                                    sx={{ mt: 1, bgcolor: 'primary.main', color: 'black' }} 
                                                />
                                            )}
                                        </CardContent>
                                        <CardActions>
                                            <Button 
                                                size="small" 
                                                href={media.url} 
                                                target="_blank"
                                                sx={{ color: 'secondary.main' }}
                                            >
                                                View Original
                                            </Button>
                                        </CardActions>
                                    </Card>
                                </Grid>
                            ))}
                        </Grid>
                    ) : (
                        <Paper 
                            sx={{ 
                                p: 4, 
                                textAlign: 'center',
                                bgcolor: 'background.default'
                            }}
                        >
                            <Typography variant="body1" color="text.secondary">
                                No results to display. Try searching for something!
                            </Typography>
                        </Paper>
                    )}
                </Grid>

                {/* Recent Searches Section (Only for logged in users) */}
                {user && (
                    <Grid item xs={12} md={4}>
                        <Paper sx={{ p: 3, bgcolor: 'background.default' }}>
                            <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
                                <HistoryIcon sx={{ mr: 1 }} />
                                Recent Searches
                            </Typography>
                            <Divider sx={{ mb: 2 }} />
                            {recentSearches.length > 0 ? (
                                <List>
                                    {recentSearches.map((search) => (
                                        <ListItem
                                            key={search.id}
                                            button
                                            onClick={() => {
                                                setQuery(search.query);
                                                handleSearch();
                                            }}
                                            sx={{
                                                mb: 1,
                                                bgcolor: 'background.paper',
                                                borderRadius: 1
                                            }}
                                        >
                                            <ListItemText 
                                                primary={search.query}
                                                secondary={new Date(search.createdAt).toLocaleString()}
                                            />
                                            <ListItemSecondaryAction>
                                                <IconButton 
                                                    edge="end" 
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        handleDeleteSearch(search.id);
                                                    }}
                                                    size="small"
                                                >
                                                    <DeleteIcon fontSize="small" />
                                                </IconButton>
                                            </ListItemSecondaryAction>
                                        </ListItem>
                                    ))}
                                </List>
                            ) : (
                                <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 2 }}>
                                    Your search history will appear here
                                </Typography>
                            )}
                        </Paper>
                    </Grid>
                )}
            </Grid>
        </Box>
    );
};

export default Home;
