// src/pages/Home.js
import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import debounce from 'lodash/debounce';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  InputAdornment,
  Button,
  Container,
  Grid,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  Chip,
  Switch,
  FormControlLabel,
  Backdrop,
  Fade,
  CircularProgress
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { Link as RouterLink } from 'react-router-dom';

import ThreeBackground from '../components/ThreeBackground';
import MediaGrid from '../components/MediaGrid';           // ← you added this
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Art', 'Nature', 'Technology', 'Architecture'];

const Home = ({ user }) => {
  const location = useLocation();
  const [darkMode, setDarkMode]   = useState(false);
  const [query, setQuery]         = useState('');
  const [results, setResults]     = useState([]);
  const [loading, setLoading]     = useState(false);
  const [searched, setSearched]   = useState(false);
  const [error, setError]         = useState('');

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? 'dark' : 'light',
          primary: { main: '#009688' }
        }
      }),
    [darkMode]
  );

  const runSearch = useCallback(
    async term => {
      const q = (term !== undefined ? term : query).trim();
      if (!q) {
        setError('Please enter a search term.');
        return;
      }
      if (q.length < 3) {
        setError('Please enter at least 3 characters.');
        return;
      }
      setError('');
      setLoading(true);
      setSearched(true);
      try {
        const { data } = await searchMedia(q);
        setResults(data.results || []);
      } catch {
        setError('Network error, please try again.');
        setResults([]);
      } finally {
        setLoading(false);
      }
    },
    [query]
  );

  const debouncedSearch = useMemo(() => debounce(runSearch, 300), [runSearch]);
  useEffect(() => () => debouncedSearch.cancel(), [debouncedSearch]);

  // if someone navigates to `/?q=foo`
  useEffect(() => {
    const q = new URLSearchParams(location.search).get('q');
    if (q && q.trim().length >= 3) {
      setQuery(q);
      runSearch(q);
    }
  }, [location.search, runSearch]);

  const handleChange = e => {
    const val = e.target.value;
    setQuery(val);
    debouncedSearch(val);
  };
  const handleSubmit = e => {
    e.preventDefault();
    debouncedSearch.cancel();
    runSearch(query);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground style={{ zIndex: -1, opacity: 0.6 }} />

      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
            LincolnVerse
          </Typography>
          <FormControlLabel
            control={
              <Switch
                checked={darkMode}
                onChange={() => setDarkMode(dm => !dm)}
                color="default"
              />
            }
            label="Dark Mode"
            sx={{ color: 'inherit' }}
          />
          {user && (
            <Typography sx={{ ml: 2, color: 'inherit' }}>
              Hi, {user.username}
            </Typography>
          )}
        </Toolbar>
      </AppBar>

      <Fade in timeout={600}>
        <Box
          sx={{
            pt: 8,
            pb: 4,
            textAlign: 'center',
            bgcolor: 'primary.main',
            color: '#fff'
          }}
        >
          <Container maxWidth="md">
            <Typography variant="h3" gutterBottom>
              Discover Open-Licensed Media
            </Typography>
            <Box
              component="form"
              onSubmit={handleSubmit}
              sx={{
                display: 'flex',
                justifyContent: 'center',
                gap: 2,
                mt: 2,
                flexWrap: 'wrap'
              }}
            >
              <TextField
                variant="filled"
                placeholder="Search media..."
                value={query}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  )
                }}
                sx={{
                  bgcolor: '#E0F2F1',
                  borderRadius: 1,
                  width: { xs: '100%', md: '50%' }
                }}
              />
              <Button
                type="submit"
                variant="contained"
                disabled={loading}
                sx={{ py: 1.5, px: 4, bgcolor: '#004D40' }}
              >
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Search'}
              </Button>
            </Box>

            {!searched && (
              <Box sx={{ mt: 4 }}>
                {trendingTopics.map(topic => (
                  <Chip
                    key={topic}
                    label={topic}
                    onClick={() => runSearch(topic)}
                    clickable
                    sx={{ m: 0.5, bgcolor: '#B2DFDB', color: '#004D40' }}
                  />
                ))}
              </Box>
            )}
          </Container>
        </Box>
      </Fade>

      <Container sx={{ py: 4 }}>
        {error && (
          <Typography color="error" sx={{ mb: 2 }}>
            {error}
          </Typography>
        )}

        {searched ? (
          loading ? (
            <Backdrop open sx={{ color: '#fff', zIndex: theme.zIndex.drawer + 1 }}>
              <CircularProgress color="inherit" />
            </Backdrop>
          ) : results.length > 0 ? (
            <Grid container spacing={3}>
              {results.map(item => (
                <Grid item key={item.id} xs={12} sm={6} md={4}>
                  <Card
                    sx={{
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column'
                    }}
                  >
                    <CardActionArea
                      component={RouterLink}
                      to={`/media/${item.asset_type || 'images'}/${item.id}`}
                    >
                      <CardMedia
                        component="img"
                        height="180"
                        image={item.thumbnail || item.url}
                        alt={item.title || 'Media'}
                        loading="lazy"
                      />
                      <CardContent>
                        <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                          {item.title || 'Untitled'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {item.creator_display_name || 'Unknown'}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Typography>No results found for “{query}”.</Typography>
          )
        ) : (
          // SHOW YOUR NEW MediaGrid HERE
          <MediaGrid />
        )}
      </Container>

      <Box sx={{ py: 2, textAlign: 'center', bgcolor: 'grey.100' }}>
        <Typography variant="body2">LincolnVerse • Noel Adesola • 2025</Typography>
      </Box>
    </ThemeProvider>
  );
};

export default Home;
