// src/components/FeaturedCategories.js
import React from 'react';
import { Grid, Card, CardActionArea, CardContent, Typography } from '@mui/material';

const cats = [
  { label: 'Landscapes', term: 'nature', bg: '#5ad4a6' },
  { label: 'Urban',     term: 'cityscape', bg: '#5e8bff' },
  { label: 'Abstract',  term: 'abstract', bg: '#ffb470' },
];

const FeaturedCategories = ({ onSelect }) => (
  <Grid container spacing={2} sx={{ mt: 4 }}>
    {cats.map(cat => (
      <Grid item xs={12} sm={4} key={cat.term}>
        <CardActionArea onClick={() => onSelect(cat.term)}>
          <Card sx={{
            bgcolor: cat.bg,
            color: '#fff',
            height: 120,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <CardContent>
              <Typography variant="h6">{cat.label}</Typography>
            </CardContent>
          </Card>
        </CardActionArea>
      </Grid>
    ))}
  </Grid>
);

export default FeaturedCategories;
