import React, { useState, useMemo } from "react";
import { loginUser } from "../services/api";
import { Link as RouterLink, useNavigate } from "react-router-dom";
import debounce from 'lodash/debounce';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  TextField,
  Button,
  Container,
  Typography,
  Paper,
  Box,
  Grid,
  InputAdornment,
  IconButton,
  Alert,
  Divider,
  Link as MuiLink,
  FormControlLabel,
  Switch
} from "@mui/material";
import Visibility from "@mui/icons-material/Visibility";
import VisibilityOff from "@mui/icons-material/VisibilityOff";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";

const Login = ({ setUser }) => {
  // Dark mode state
  const [darkMode, setDarkMode] = useState(false);
  // Teal as primary color
  const theme = useMemo(
    () => createTheme({
      palette: {
        mode: darkMode ? 'dark' : 'light',
        primary: { main: '#009688' }
      }
    }),
    [darkMode]
  );

  const [formData, setFormData] = useState({ username: "", password: "" });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError("");
  };

  const togglePassword = () => setShowPassword(show => !show);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      await loginUser(formData);
      setUser({ username: formData.username });
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.error || "Sign in failed. Please check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="xs">
        <Box mt={4} display="flex" justifyContent="flex-end">
          <FormControlLabel
            control={
              <Switch
                checked={darkMode}
                onChange={() => setDarkMode(!darkMode)}
                inputProps={{ 'aria-label': 'Toggle dark mode' }}
              />
            }
            label="Dark Mode"
          />
        </Box>
        <Box display="flex" flexDirection="column" alignItems="center" mt={2}>
          <Paper elevation={3} sx={{ p: 4, width: '100%', borderTop: 4, borderColor: 'primary.main' }}>
            <Box display="flex" flexDirection="column" alignItems="center" mb={2}>
              <Box
                sx={{
                  bgcolor: 'primary.main',
                  color: 'primary.contrastText',
                  borderRadius: '50%',
                  p: 1,
                  mb: 1
                }}
              >
                <LockOutlinedIcon fontSize="large" />
              </Box>
              <Typography component="h1" variant="h5" gutterBottom>
                LincolnVerse Sign In
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Access your LincolnVerse account
              </Typography>
            </Box>

            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

            <Box component="form" onSubmit={handleSubmit} noValidate>
              <TextField
                margin="normal"
                required
                fullWidth
                id="username"
                name="username"
                label="Username"
                autoComplete="username"
                autoFocus
                value={formData.username}
                onChange={handleChange}
                InputProps={{
                  'aria-label': 'username',
                  startAdornment: (
                    <InputAdornment position="start">
                      <PersonOutlineIcon />
                    </InputAdornment>
                  )
                }}
              />
              <TextField
                margin="normal"
                required
                fullWidth
                id="password"
                name="password"
                label="Password"
                type={showPassword ? 'text' : 'password'}
                autoComplete="current-password"
                value={formData.password}
                onChange={handleChange}
                InputProps={{
                  'aria-label': 'password',
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockOutlinedIcon />
                    </InputAdornment>
                  ),
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        aria-label={showPassword ? 'Hide password' : 'Show password'}
                        onClick={togglePassword}
                        edge="end"
                      >
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  )
                }}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, py: 1.5, backgroundColor: 'primary.main' }}
                disabled={loading}
              >
                {loading ? 'Signing in...' : 'Sign In'}
              </Button>

              <Divider sx={{ my: 2 }}>Or</Divider>

              <Grid container justifyContent="center">
                <Grid item>
                  <Typography variant="body2">
                    Don't have an account?{' '}
                    <MuiLink component={RouterLink} to="/register" underline="always" sx={{ color: 'primary.main' }}>
                      Sign Up
                    </MuiLink>
                  </Typography>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default Login;
