// src/pages/Register.js
import React, { useState, useMemo } from 'react';
import { registerUser } from '../services/api';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  Container,
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  InputAdornment,
  IconButton,
  Alert,
  Divider,
  FormControlLabel,
  Switch,
  Backdrop,
  Fade,
  CircularProgress
} from '@mui/material';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import EmailIcon from '@mui/icons-material/Email';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import ThreeBackground from '../components/ThreeBackground';

export default function Register() {
  const [darkMode, setDarkMode] = useState(false);
  const theme = useMemo(
    () => createTheme({
      palette: { mode: darkMode ? 'dark' : 'light', primary: { main: '#009688' } }
    }),
    [darkMode]
  );

  const [formData, setFormData] = useState({ username: '', email: '', password: '', confirmPassword: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError('');
  };
  const togglePassword = () => setShowPassword(prev => !prev);

  const validate = () => {
    if (!formData.username.trim()) { setError('Username is required'); return false; }
    if (formData.password.length < 6) { setError('Password must be at least 6 characters'); return false; }
    if (formData.password !== formData.confirmPassword) { setError('Passwords do not match'); return false; }
    return true;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validate()) return;
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      await registerUser({ username: formData.username, email: formData.email, password: formData.password });
      setSuccess('Registration successful! Redirecting to login...');
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.1 }} />
      <Container maxWidth="sm">
        <Box mt={4} display="flex" justifyContent="flex-end">
          <FormControlLabel
            control={<Switch checked={darkMode} onChange={() => setDarkMode(prev => !prev)} color="default" />}
            label="Dark Mode"
            sx={{ color: 'text.primary' }}
          />
        </Box>
        <Fade in timeout={600}>
          <Box display="flex" flexDirection="column" alignItems="center" mt={2}>
            <Paper elevation={3} sx={{ p: 4, width: '100%', borderTop: 4, borderColor: 'primary.main', position: 'relative' }}>
              {loading && <Backdrop open sx={{ position: 'absolute', color: '#fff', zIndex: theme.zIndex.drawer + 1 }}><CircularProgress /></Backdrop>}
              <Box display="flex" flexDirection="column" alignItems="center" mb={2}>
                <Box sx={{ bgcolor: 'primary.main', color: 'primary.contrastText', p: 1, borderRadius: '50%', mb: 1 }}>
                  <PersonAddIcon fontSize="large" />
                </Box>
                <Typography variant="h5" gutterBottom>Create Account</Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>Sign up to LincolnVerse</Typography>
              </Box>
              {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
              {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
              <Box component="form" onSubmit={handleSubmit} noValidate>
                <TextField
                  margin="normal" required fullWidth name="username" label="Username" autoComplete="username" autoFocus
                  value={formData.username} onChange={handleChange}
                  InputProps={{ startAdornment: <InputAdornment position="start"><PersonOutlineIcon /></InputAdornment> }}
                />
                <TextField
                  margin="normal" fullWidth name="email" label="Email (Optional)" autoComplete="email"
                  value={formData.email} onChange={handleChange}
                  InputProps={{ startAdornment: <InputAdornment position="start"><EmailIcon /></InputAdornment> }}
                />
                <TextField
                  margin="normal" required fullWidth name="password" label="Password" type={showPassword ? 'text' : 'password'} autoComplete="new-password"
                  value={formData.password} onChange={handleChange}
                  InputProps={{ endAdornment: <InputAdornment position="end"><IconButton onClick={togglePassword} edge="end">{showPassword ? <VisibilityOff /> : <Visibility />}</IconButton></InputAdornment> }}
                />
                <TextField
                  margin="normal" required fullWidth name="confirmPassword" label="Confirm Password" type={showPassword ? 'text' : 'password'}
                  value={formData.confirmPassword} onChange={handleChange}
                  InputProps={{ endAdornment: <InputAdornment position="end"><IconButton onClick={togglePassword} edge="end">{showPassword ? <VisibilityOff /> : <Visibility />}</IconButton></InputAdornment> }}
                />
                <Button type="submit" fullWidth variant="contained" color="primary" sx={{ mt: 3, mb: 2, py: 1.5 }} disabled={loading}>
                  {loading ? 'Creating Account...' : 'Create Account'}
                </Button>
                <Divider sx={{ my: 2 }}>Or</Divider>
                <Grid container justifyContent="center">
                  <Grid item>
                    <Typography variant="body2">
                      Already have an account? <RouterLink to="/login" style={{ fontWeight: 'bold', color: theme.palette.primary.main }}>Sign In</RouterLink>
                    </Typography>
                  </Grid>
                </Grid>
              </Box>
            </Paper>
          </Box>
        </Fade>
      </Container>
    </ThemeProvider>
  );
}
