import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActionArea,
  InputAdornment,
  CircularProgress
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';
import MediaGrid from '../components/MediaGrid';

const Home = ({ user }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setLoading(true);
    setSearched(true);
    try {
      const data = await searchMedia(query);
      setResults(data.results || []);
    } catch {
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.5 }} />

      <AppBar position="fixed" elevation={0} sx={{ backgroundColor: 'transparent' }}>
        <Toolbar sx={{ justifyContent: 'space-between' }}>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            Media Explorer
          </Typography>
          {user && (
            <Typography variant="subtitle1">
              Hi, {user.username}
            </Typography>
          )}
        </Toolbar>
      </AppBar>

      <Box
        component="section"
        sx={{
          pt: 14,
          pb: 8,
          textAlign: 'center',
          color: 'common.white',
          background: 'linear-gradient(to bottom, rgba(30, 30, 30, 0.7), rgba(30, 30, 30, 0.9))'
        }}
      >
        <Container maxWidth="md">
          <Typography variant="h2" sx={{ fontWeight: 700, mb: 2, fontFamily: 'Roboto, sans-serif' }}>
            Discover Open-Licensed Media
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, fontWeight: 300 }}>
            A CMP9134 Software Engineering project using the Openverse API
          </Typography>

          <Box component="form" onSubmit={handleSearch} sx={{ display: 'flex', gap: 1, justifyContent: 'center', flexWrap: 'wrap' }}>
            <TextField
              variant="outlined"
              placeholder="Search for images, audio, video..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              sx={{
                width: { xs: '100%', md: '60%' },
                backgroundColor: 'background.paper',
                borderRadius: 8,
                '& .MuiOutlinedInput-root': { borderRadius: 8 }
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                )
              }}
            />
            <Button type="submit" variant="contained" disabled={loading} sx={{ py: 1.5, px: 4, borderRadius: 6 }}>
              {loading ? <CircularProgress size={20} color="inherit" /> : 'Search'}
            </Button>
          </Box>
        </Container>
      </Box>

      <Container component="main" sx={{ pt: 6, pb: 6, minHeight: '60vh' }} maxWidth="lg">
        {searched ? (
          results.length ? (
            <Grid container spacing={4}>
              {results.map((item) => (
                <Grid item xs={12} sm={6} md={4} key={item.id}>
                  <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                    <CardActionArea component="a" href={item.url} target="_blank">
                      <CardMedia
                        component="img"
                        height="180"
                        image={item.thumbnail || item.url}
                        alt={item.title}
                      />
                      <CardContent>
                        <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                          {item.title || 'Untitled'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {item.creator_display_name || 'Unknown'}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Typography variant="body1" color="text.secondary" sx={{ textAlign: 'center' }}>
              No results found for "{query}".
            </Typography>
          )
        ) : (
          <MediaGrid />
        )}
      </Container>

      <Box component="footer" sx={{ py: 4, textAlign: 'center', backgroundColor: 'grey.100' }}>
        <Typography variant="body2">
          CMP9134 Software Engineering &bull; University of Lincoln &bull; 2025
        </Typography>
      </Box>
    </>
  );
};

export default Home;
