// src/pages/Home.js
import React, { useState, useMemo, useCallback, useEffect } from 'react';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  InputAdornment,          // ← ensure this is imported
  Button,
  Container,
  Chip,
  Switch,
  FormControlLabel,
  CircularProgress,
  Fade
} from '@mui/material';
import { Link as RouterLink, useNavigate, useLocation } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';
import debounce from 'lodash/debounce';

import ThreeBackground from '../components/ThreeBackground';
import MediaGrid from '../components/MediaGrid';      // ← import MediaGrid
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Art', 'Nature', 'Technology', 'Architecture'];
const PER_PAGE = 20;

const Home = ({ user }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const [darkMode, setDarkMode] = useState(false);
  const [query, setQuery]       = useState('');
  const [results, setResults]   = useState([]);
  const [page, setPage]         = useState(1);
  const [loading, setLoading]   = useState(false);
  const [searched, setSearched] = useState(false);
  const [error, setError]       = useState('');

  const theme = useMemo(() =>
    createTheme({
      palette: {
        mode: darkMode ? 'dark' : 'light',
        primary: { main: '#009688' }
      }
    }),
  [darkMode]);

  // Fetch (and paginate) results
  const runSearch = useCallback(async (term, pageNum = 1, append = false) => {
    const q = (term !== undefined ? term : query).trim();
    if (!q) {
      setError('Please enter a search term.');
      return;
    }
    if (q.length < 3) {
      setError('Please enter at least 3 characters.');
      return;
    }
    setError('');
    setLoading(true);
    setSearched(true);

    try {
      const { data } = await searchMedia(q, 'images', pageNum, PER_PAGE);
      const hits = data.results || [];
      setResults(curr => append ? [...curr, ...hits] : hits);
      setPage(pageNum);
    } catch {
      setError('Network error, please try again.');
      if (!append) setResults([]);
    } finally {
      setLoading(false);
    }
  }, [query]);

  // Debounce live‐typing
  const debounced = useMemo(() => debounce(runSearch, 300), [runSearch]);
  useEffect(() => () => debounced.cancel(), [debounced]);

  // Auto-run if URL has ?q=…
  useEffect(() => {
    const q = new URLSearchParams(location.search).get('q');
    if (q && q.trim().length >= 3) {
      setQuery(q);
      runSearch(q, 1, false);
    }
  }, [location.search, runSearch]);

  const handleChange = e => {
    const val = e.target.value;
    setQuery(val);
    debounced(val, 1, false);
  };

  const handleSubmit = e => {
    e.preventDefault();
    debounced.cancel();
    runSearch(query, 1, false);
  };

  const handleLoadMore = () => runSearch(query, page + 1, true);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />

      {/* Animated gradient background */}
      <ThreeBackground />

      {/* Navbar with Login/Signup */}
      <AppBar position="static" color="transparent" elevation={0}>
        <Toolbar>
          <Typography
            component={RouterLink}
            to="/"
            variant="h5"
            sx={{
              flexGrow: 1,
              textDecoration: 'none',
              fontWeight: 700,
              color: theme.palette.text.primary
            }}
          >
            LincolnVerse
          </Typography>

          {user ? (
            <Typography sx={{ mr: 2, color: theme.palette.text.primary }}>
              Hi, {user.username}
            </Typography>
          ) : (
            <>
              <Button component={RouterLink} to="/login" sx={{ color: theme.palette.text.primary }}>
                Login
              </Button>
              <Button
                component={RouterLink}
                to="/register"
                variant="contained"
                sx={{ ml: 1, bgcolor: theme.palette.primary.main }}
              >
                Sign Up
              </Button>
            </>
          )}

          <FormControlLabel
            control={
              <Switch
                checked={darkMode}
                onChange={() => setDarkMode(dm => !dm)}
                color="default"
              />
            }
            label="Dark Mode"
            sx={{ ml: 2, color: theme.palette.text.primary }}
          />
        </Toolbar>
      </AppBar>

      {/* Hero */}
      <Fade in timeout={600}>
        <Box sx={{ pt: 8, pb: 4, textAlign: 'center' /* no bgcolor override */ }}>
          <Container maxWidth="sm">
            <Typography variant="h3" gutterBottom>
              Discover Open-Licensed Media
            </Typography>
            <Box
              component="form"
              onSubmit={handleSubmit}
              sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 2, flexWrap: 'wrap' }}
            >
              <TextField
                variant="filled"
                placeholder="Search media…"
                value={query}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  )
                }}
                sx={{ bgcolor: '#fff', width: { xs: '100%', md: '60%' } }}
              />
              <Button type="submit" variant="contained" disabled={loading}>
                {loading ? <CircularProgress size={20} /> : 'Search'}
              </Button>
            </Box>

            {!searched && (
              <Box sx={{ mt: 4 }}>
                {trendingTopics.map(topic => (
                  <Chip
                    key={topic}
                    label={topic}
                    onClick={() => runSearch(topic, 1, false)}
                    clickable
                    sx={{
                      m: 0.5,
                      bgcolor: theme.palette.primary.light,
                      color: theme.palette.primary.contrastText
                    }}
                  />
                ))}
              </Box>
            )}
          </Container>
        </Box>
      </Fade>

      {/* Results grid */}
      <Container sx={{ py: 4 }}>
        {error && (
          <Typography color="error" sx={{ mb: 2 }}>
            {error}
          </Typography>
        )}

        <MediaGrid
          items={results}
          loading={loading}
          onItemClick={item =>
            navigate(`/media/${item.asset_type || 'images'}/${item.id}`)
          }
          onLoadMore={searched && results.length === page * PER_PAGE ? handleLoadMore : null}
        />

        {!searched && (
          <Box textAlign="center" mt={4}>
            <Button onClick={() => runSearch('', 1, false)}>
              Explore Trending
            </Button>
          </Box>
        )}
      </Container>

      {/* Footer */}
      <Box sx={{ py: 2, textAlign: 'center', bgcolor: 'grey.100' }}>
        <Typography variant="body2">LincolnVerse • Noel Adesola • 2025</Typography>
      </Box>
    </ThemeProvider>
  );
};

export default Home;
