import React, { useState, useMemo } from 'react';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Divider,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActionArea,
  InputAdornment,
  CircularProgress,
  Chip,
  Switch,
  Skeleton,
  Pagination
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FilterListIcon from '@mui/icons-material/FilterList';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Design', 'Nature', 'Space', 'Textures'];
const rowsPerPage = 12;

const Home = ({ user }) => {
  // Theme and accessibility state
  const [darkMode, setDarkMode] = useState(false);
  const [largeFont, setLargeFont] = useState(false);
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? 'dark' : 'light',
          primary: { main: '#009688' }
        },
        typography: {
          fontSize: largeFont ? 16 : 14
        }
      }),
    [darkMode, largeFont]
  );

  // Drawer state for filters and accessibility
  const [filterOpen, setFilterOpen] = useState(false);
  const [accessOpen, setAccessOpen] = useState(false);

  // Filter state
  const [license, setLicense] = useState('');
  const [mediaType, setMediaType] = useState('');

  // Search state
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  // Pagination state
  const [page, setPage] = useState(1);
  const handlePageChange = (e, value) => setPage(value);

  // Favorites state
  const [favorites, setFavorites] = useState(new Set());
  const toggleFavorite = (id) => {
    setFavorites((prev) => {
      const next = new Set(prev);
      next.has(id) ? next.delete(id) : next.add(id);
      return next;
    });
  };

  // Run search with optional filters
  const runSearch = async (term) => {
    const q = term || query;
    if (!q.trim()) return;
    setLoading(true);
    setSearched(true);
    setQuery(q);
    try {
      const data = await searchMedia(q, { license, mediaType });
      setResults(data.results || []);
      setPage(1);
    } catch {
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    runSearch();
  };

  // Paginated slice
  const pageCount = Math.ceil(results.length / rowsPerPage);
  const paginated = results.slice((page - 1) * rowsPerPage, page * rowsPerPage);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.3 }} />

      {/* AppBar with theme and drawer toggles */}
      <AppBar position="fixed" elevation={1} sx={{ backgroundColor: 'primary.main' }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
            LincolnVerse
          </Typography>
          <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
          <IconButton color="inherit" onClick={() => setFilterOpen(true)}>
            <FilterListIcon />
          </IconButton>
          <IconButton color="inherit" onClick={() => setAccessOpen(true)}>
            <AccessibilityNewIcon />
          </IconButton>
          {user && <Typography sx={{ ml: 2 }}>{`Hi, ${user.username}`}</Typography>}
        </Toolbar>
      </AppBar>

      {/* Filter Drawer */}
      <Drawer anchor="left" open={filterOpen} onClose={() => setFilterOpen(false)}>
        <Box sx={{ width: 240, p: 2 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>Filters</Typography>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel>License</InputLabel>
            <Select value={license} label="License" onChange={(e) => setLicense(e.target.value)}>
              <MenuItem value="">All</MenuItem>
              <MenuItem value="cc-by">CC BY</MenuItem>
              <MenuItem value="cc0">CC0</MenuItem>
              <MenuItem value="public-domain">Public Domain</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel>Media Type</InputLabel>
            <Select value={mediaType} label="Media Type" onChange={(e) => setMediaType(e.target.value)}>
              <MenuItem value="">All</MenuItem>
              <MenuItem value="image">Image</MenuItem>
              <MenuItem value="audio">Audio</MenuItem>
              <MenuItem value="video">Video</MenuItem>
            </Select>
          </FormControl>
          <Button variant="contained" fullWidth onClick={() => { setFilterOpen(false); runSearch(); }}>
            Apply Filters
          </Button>
        </Box>
      </Drawer>

      {/* Accessibility Drawer */}
      <Drawer anchor="right" open={accessOpen} onClose={() => setAccessOpen(false)}>
        <Box sx={{ width: 240, p: 2 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>Accessibility</Typography>
          <List>
            <ListItem>
              <ListItemText primary="Large Font" />
              <Switch checked={largeFont} onChange={() => setLargeFont(!largeFont)} />
            </ListItem>
          </List>
        </Box>
      </Drawer>

      {/* Hero Section */}
      <Box
        component="section"
        sx={{ pt: 14, pb: 6, textAlign: 'center', color: 'common.white', background: 'linear-gradient(to bottom, #004d40, #00796b)' }}
      >
        <Container maxWidth="md">
          <Typography variant="h2" sx={{ fontWeight: 700, mb: 2 }}>Explore Media</Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
            <TextField
              variant="filled"
              placeholder="Search media..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              sx={{ width: { xs: '100%', md: '60%' }, bgcolor: 'background.paper', borderRadius: 1 }}
              InputProps={{ startAdornment: (<InputAdornment position="start"><SearchIcon /></InputAdornment>) }}
            />
            <Button type="submit" variant="contained" disabled={loading} sx={{ py: 1.5, px: 4 }}>
              {loading ? <CircularProgress size={20} /> : 'Search'}
            </Button>
          </Box>
        </Container>
      </Box>

      {/* Main Content */}
      <Container component="main" sx={{ pt: 4, pb: 6 }} maxWidth="lg">
        {searched ? (
          loading ? (
            <Grid container spacing={4}>
              {Array.from({ length: rowsPerPage }).map((_, idx) => (
                <Grid item xs={12} sm={6} md={4} key={idx}>
                  <Skeleton variant="rectangular" height={200} />
                  <Skeleton width="60%" sx={{ mt: 1 }} />
                  <Skeleton width="40%" />
                </Grid>
              ))}
            </Grid>
          ) : paginated.length ? (
            <>
              <Grid container spacing={4}>
                {paginated.map((item) => (
                  <Grid item xs={12} sm={6} md={4} key={item.id}>
                    <Card sx={{ height: '100%', position: 'relative' }}>
                      <IconButton
                        onClick={() => toggleFavorite(item.id)}
                        sx={{ position: 'absolute', top: 8, right: 8, bgcolor: 'background.paper', '&:hover': { bgcolor: 'grey.200' } }}
                      >
                        {favorites.has(item.id) ? <FavoriteIcon color="error" /> : <FavoriteBorderIcon />}
                      </IconButton>
                      <CardActionArea component="a" href={item.url} target="_blank">
                        <CardMedia component="img" height="180" image={item.thumbnail || item.url} alt={item.title} />
                        <CardContent>
                          <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>{item.title || 'Untitled'}</Typography>
                          <Typography variant="body2" color="text.secondary">{item.creator_display_name || 'Unknown'}</Typography>
                        </CardContent>
                      </CardActionArea>
                    </Card>
                  </Grid>
                ))}
              </Grid>
              <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                <Pagination count={pageCount} page={page} onChange={handlePageChange} color="primary" />
              </Box>
            </>
          ) : (
            <Typography variant="h6" align="center">No results found for "{query}".</Typography>
          )
        ) : (
          <>
            <Typography variant="h4" sx={{ mb: 2 }}>Trending Now</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {trendingTopics.map((topic) => (
                <Chip key={topic} label={topic} onClick={() => runSearch(topic)} clickable color="primary" />
              ))}
            </Box>
          </>
        )}
      </Container>

      {/* Footer */}
      <Box component="footer" sx={{ py: 4, textAlign: 'center', bgcolor: 'background.paper' }}>
        <Typography variant="body2">CMP9134 Software Engineering • University of Lincoln • 2025</Typography>
      </Box>
    </ThemeProvider>
  );
};

export default Home;
