// src/pages/MediaDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom';
import { getMediaDetails } from '../services/api';
import {
  Container,
  Box,
  Typography,
  Button,
  CircularProgress,
  CardMedia,
  Alert,
  Link as MuiLink
} from '@mui/material';

const MediaDetail = () => {
  const { type, id } = useParams();        // e.g. type="images", id="12345"
  const navigate = useNavigate();
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    setLoading(true);
    getMediaDetails(type, id)
      .then(res => {
        setMedia(res.data);
        setError('');
      })
      .catch(() => {
        setError('Failed to load media details. Please try again.');
      })
      .finally(() => {
        setLoading(false);
      });
  }, [type, id]);

  if (loading) {
    return (
      <Container sx={{ py: 4, textAlign: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container sx={{ py: 4 }}>
        <Alert severity="error">{error}</Alert>
        <Box mt={2}>
          <Button variant="outlined" onClick={() => navigate(-1)}>
            &larr; Back
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container sx={{ py: 4 }}>
      <Box mb={3}>
        <Button variant="outlined" onClick={() => navigate(-1)}>
          &larr; Back to results
        </Button>
      </Box>
      <Typography variant="h4" gutterBottom>
        {media.title || 'Untitled'}
      </Typography>
      <Typography variant="subtitle2" color="text.secondary" gutterBottom>
        {media.creator_display_name && (
          <>
            By{' '}
            <MuiLink
              href={media.creator_url}
              target="_blank"
              rel="noopener"
            >
              {media.creator_display_name}
            </MuiLink>
          </>
        )}
      </Typography>

      <CardMedia
        component={type === 'audio' ? 'audio' : type === 'video' ? 'video' : 'img'}
        src={media.url}
        controls={type !== 'images'}
        height={type === 'images' ? 400 : undefined}
        alt={media.title}
        sx={{ width: '100%', maxHeight: 600, objectFit: 'contain', mb: 2 }}
      />

      <Box display="flex" flexWrap="wrap" gap={2} mb={2}>
        <Button
          variant="contained"
          component="a"
          href={media.url}
          target="_blank"
          rel="noopener"
        >
          Download
        </Button>
        {media.license_url && (
          <Button
            variant="outlined"
            component="a"
            href={media.license_url}
            target="_blank"
            rel="noopener"
          >
            View License
          </Button>
        )}
      </Box>

      {media.description && (
        <Typography paragraph>{media.description}</Typography>
      )}
    </Container>
  );
};

export default MediaDetail;
