import axios from 'axios';

const API = axios.create({
    baseURL: 'http://localhost:5000',
    withCredentials: true // Allows session-based authentication
});

// Authentication API
export const registerUser = (userData) => API.post('/auth/register', userData);
export const loginUser = (userData) => API.post('/auth/login', userData);
export const logoutUser = () => API.post('/auth/logout');

// Search API
export const searchMedia = (query) => axios.get(`https://api.openverse.org/v1/images/?q=${query}`);
export const saveSearch = (query) => API.post('/search/save', { query });
export const getRecentSearches = () => API.get('/search/recent');
export const deleteSearch = (id) => API.delete(`/search/${id}`);

export default API;
