import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  InputAdornment,
  CircularProgress,
  Fade,
  Chip
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';
import MediaGrid from '../components/MediaGrid';

const popularTopics = ['Nature', 'Technology', 'Architecture', 'Art', 'Animals'];

const Home = ({ user }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const runSearch = async (term) => {
    const q = term || query;
    if (!q.trim()) return;
    setLoading(true);
    setSearched(true);
    try {
      const data = await searchMedia(q);
      setResults(data.results || []);
      setQuery(q);
    } catch (error) {
      console.error(error);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    runSearch();
  };

  return (
    <>
      <ThreeBackground style={{ zIndex: -1, opacity: 0.6 }} />

      <Box sx={{ pt: 8, pb: 12, position: 'relative', textAlign: 'center' }}>
        <Container maxWidth="lg">
          <Fade in timeout={800}>
            <Typography variant="h3" component="h1" sx={{ fontWeight: 700, color: 'white', mb: 2 }}>
              Discover Open-Licensed Media
            </Typography>
          </Fade>
          <Fade in timeout={1000}>
            <Box
              component="form"
              onSubmit={handleSubmit}
              sx={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                gap: 2,
                mt: 4,
                flexWrap: 'wrap'
              }}
            >
              <TextField
                variant="filled"
                placeholder="Search images, audio, video..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                sx={{ backgroundColor: 'rgba(255,255,255,0.9)', borderRadius: 1, width: { xs: '100%', sm: '70%' } }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                  disableUnderline: true
                }}
              />
              <Button type="submit" variant="contained" disabled={loading} sx={{ py: 1.5, px: 4 }}>
                {loading ? <CircularProgress size={20} color="inherit" /> : 'Search'}
              </Button>
            </Box>
          </Fade>

          {!searched && (
            <Fade in timeout={1200}>
              <Box sx={{ mt: 6, display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: 1 }}>
                {popularTopics.map((topic) => (
                  <Chip
                    key={topic}
                    label={topic}
                    onClick={() => runSearch(topic)}
                    clickable
                  />
                ))}
              </Box>
            </Fade>
          )}
        </Container>
      </Box>

      <Container sx={{ py: 6 }} maxWidth="lg">
        <Grid container spacing={4}>
          <Grid item xs={12} md={4}>
            <Box sx={{ mb: 4 }}>
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 2 }}>
                {searched ? 'Your Search' : 'Hot Topics'}
              </Typography>
              {!searched ? (
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                  {popularTopics.map((topic) => (
                    <Button key={topic} variant="outlined" onClick={() => runSearch(topic)}>
                      {topic}
                    </Button>
                  ))}
                </Box>
              ) : (
                <Typography variant="body2" color="text.secondary">
                  Showing results for "{query}"
                </Typography>
              )}
            </Box>
          </Grid>

          <Grid item xs={12} md={8}>
            {searched ? (
              results.length ? (
                <Grid container spacing={3}>
                  {results.map((item) => (
                    <Grid item xs={12} sm={6} key={item.id}>
                      <Card>
                        <CardActionArea href={item.url} target="_blank">
                          <CardMedia
                            component="img"
                            height="180"
                            image={item.thumbnail || item.url}
                            alt={item.title}
                          />
                          <CardContent>
                            <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                              {item.title || 'Untitled'}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              {item.creator_display_name || 'Unknown'}
                            </Typography>
                          </CardContent>
                        </CardActionArea>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              ) : (
                <Typography variant="body1" color="text.secondary">
                  No results found for "{query}".
                </Typography>
              )
            ) : (
              <MediaGrid />
            )}
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default Home;
