// src/components/ThreeBackground.js
import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, MeshDistortMaterial } from '@react-three/drei';

// Animated sphere component
const AnimatedSphere = ({ position, color, speed, distort }) => {
  const sphereRef = useRef();
  
  useFrame(({ clock }) => {
    const t = clock.getElapsedTime() * speed;
    sphereRef.current.position.y = Math.sin(t) * 0.5;
    sphereRef.current.rotation.x = t * 0.5;
    sphereRef.current.rotation.z = t * 0.3;
  });
  
  return (
    <Sphere ref={sphereRef} args={[1, 64, 64]} position={position}>
      <MeshDistortMaterial 
        color={color} 
        attach="material" 
        distort={distort} 
        speed={2} 
        roughness={0.5}
        metalness={0.3}
      />
    </Sphere>
  );
};

// Main background component
const ThreeBackground = ({ style }) => {
  return (
    <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', ...style }}>
      <Canvas camera={{ position: [0, 0, 5], fov: 75 }}>
        <ambientLight intensity={0.7} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <pointLight position={[-10, -10, -10]} intensity={0.5} color="#e1c5ff" />
        
        <AnimatedSphere position={[-3, 0, 0]} color="#5e8bff" speed={0.7} distort={0.4} />
        <AnimatedSphere position={[3, 0, -2]} color="#5ad4a6" speed={0.5} distort={0.3} />
        <AnimatedSphere position={[0, 2, -5]} color="#ffb470" speed={0.3} distort={0.2} />
        
        <OrbitControls enableZoom={false} enablePan={false} autoRotate rotateSpeed={0.5} />
      </Canvas>
    </div>
  );
};

export default ThreeBackground;
