// src/components/MediaGrid.js
import React, { useState } from 'react';
import {
  Grid,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  Typography,
  Box,
  Button,
  Stack
} from '@mui/material';

const MediaGrid = () => {
  // Sample media items
  const mediaItems = [
    { id: 1, title: 'Beautiful mountain landscape',      type: 'Image', imageUrl: 'https://media.istockphoto.com/id/485371557/photo/twilight-at-spirit-island.jpg' },
    { id: 2, title: 'Ocean waves at sunset',             type: 'Image', imageUrl: 'https://i.etsystatic.com/6725214/r/il/ecc5e0/559845979/il_1080xN.559845979_bssc.jpg' },
    { id: 3, title: 'Urban cityscape photography',       type: 'Image', imageUrl: 'https://i.pinimg.com/474x/25/b3/09/25b309014a8d416f87407fce3ed8a807.jpg' },
    { id: 4, title: 'Wildlife in natural habitat',       type: 'Image', imageUrl: 'https://media.istockphoto.com/id/1495939426/photo/puffins-against-coastline.jpg' },
    { id: 5, title: 'Abstract digital art piece',        type: 'Image', imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWKlsSrPqmMEiYjD7jsciOBs6jPnq1G_6iC0jNnjIEyRL6BoJuKjSXVl0&s' },
    { id: 6, title: 'Futuristic technology concept',     type: 'Image', imageUrl: 'https://plus.unsplash.com/premium_photo-1677269465314-d5d2247a0b0c' },
    { id: 7, title: 'Night sky with stars',              type: 'Image', imageUrl: 'https://images.unsplash.com/photo-1501785888041-af3ef285b470' },
    { id: 8, title: 'Calm forest river',                 type: 'Image', imageUrl: 'https://images.unsplash.com/photo-1504198458649-3128b932f49f' },
    { id: 9, title: 'Desert dunes at sunrise',           type: 'Image', imageUrl: 'https://images.unsplash.com/photo-1504384308090-c894fdcc538d' },
  ];

  const pageSize = 3;
  const totalPages = Math.ceil(mediaItems.length / pageSize);

  const [page, setPage] = useState(1);

  // items to display on current page
  const visibleItems = mediaItems.slice( (page - 1) * pageSize, page * pageSize );

  const handleLoadMore = () => {
    setPage(p => Math.min(p + 1, totalPages));
  };

  const handlePageClick = (p) => {
    setPage(p);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <Box sx={{ py: 4, px: 2 }}>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 600 }}>
        Trending Media
      </Typography>
      <Typography variant="body1" color="text.secondary" gutterBottom>
        Explore popular and trending creative assets
      </Typography>

      <Grid container spacing={3}>
        {visibleItems.map(item => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card sx={{ height: '100%' }}>
              <CardActionArea>
                <CardMedia
                  component="img"
                  height="200"
                  image={item.imageUrl}
                  alt={item.title}
                  sx={{ objectFit: 'cover' }}
                />
                <CardContent>
                  <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                    {item.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {item.type}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Load More button */}
      {page < totalPages && (
        <Box textAlign="center" mt={4}>
          <Button variant="outlined" onClick={handleLoadMore}>
            Load More
          </Button>
        </Box>
      )}

      {/* Page numbers */}
      <Stack direction="row" spacing={1} justifyContent="center" mt={2}>
        {Array.from({ length: totalPages }, (_, i) => (
          <Button
            key={i + 1}
            size="small"
            variant={i + 1 === page ? 'contained' : 'text'}
            onClick={() => handlePageClick(i + 1)}
          >
            {i + 1}
          </Button>
        ))}
      </Stack>
    </Box>
  );
};

export default MediaGrid;
