import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActionArea,
  InputAdornment,
  CircularProgress,
  Chip
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Sunsets', 'Urban', 'Wildlife', 'Abstract', 'Mountains'];

const Home = ({ user }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const runSearch = async (term) => {
    const q = term || query;
    if (!q.trim()) return;
    setLoading(true);
    setSearched(true);
    setQuery(q);
    try {
      const data = await searchMedia(q);
      setResults(data.results || []);
    } catch {
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    runSearch();
  };

  return (
    <>
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.5 }} />

      <AppBar position="fixed" elevation={0} sx={{ backgroundColor: 'primary.dark' }}>
        <Toolbar sx={{ justifyContent: 'space-between' }}>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            Noel Verse
          </Typography>
          {user && <Typography variant="subtitle1">Hi, {user.username}</Typography>}
        </Toolbar>
      </AppBar>

      <Box
        component="section"
        sx={{
          pt: 14,
          pb: 8,
          textAlign: 'center',
          color: 'common.white',
          background: 'linear-gradient(to bottom, #3f51b5, #283593)'
        }}
      >
        <Container maxWidth="md">
          <Typography variant="h2" sx={{ fontWeight: 700, mb: 2, fontFamily: 'Montserrat, sans-serif' }}>
            Welcome to Noel Verse
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, fontWeight: 300, fontFamily: 'Roboto, sans-serif' }}>
            Explore open-license images, audio, and more
          </Typography>

          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
            <TextField
              variant="filled"
              placeholder="Search for media..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              sx={{
                width: { xs: '100%', md: '60%' },
                backgroundColor: 'rgba(255,255,255,0.9)',
                borderRadius: 3,
                '.MuiFilledInput-root': { borderRadius: 3 }
              }}
              InputProps={{ startAdornment: <InputAdornment position="start"><SearchIcon /></InputAdornment> }}
            />
            <Button type="submit" variant="contained" disabled={loading} sx={{ py: 1.5, px: 4, borderRadius: 3 }}>
              {loading ? <CircularProgress size={20} color="inherit" /> : 'Search'}
            </Button>
          </Box>
        </Container>
      </Box>

      <Container component="main" sx={{ pt: 6, pb: 6, minHeight: '60vh' }} maxWidth="lg">
        {searched ? (
          results.length ? (
            <Grid container spacing={4}>
              {results.map((item) => (
                <Grid item xs={12} sm={6} md={4} key={item.id}>
                  <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                    <CardActionArea component="a" href={item.url} target="_blank">
                      <CardMedia component="img" height="180" image={item.thumbnail || item.url} alt={item.title} />
                      <CardContent>
                        <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                          {item.title || 'Untitled'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {item.creator_display_name || 'Unknown'}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Typography variant="body1" color="text.secondary" sx={{ textAlign: 'center' }}>
              No results found for "{query}".
            </Typography>
          )
        ) : (
          <>
            <Typography variant="h4" sx={{ fontWeight: 600, mb: 2 }}>Trending Now</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 4 }}>
              {trendingTopics.map((topic) => (
                <Chip key={topic} label={topic} onClick={() => runSearch(topic)} clickable color="primary" />
              ))}
            </Box>
          </>
        )}
      </Container>

      <Box component="footer" sx={{ py: 4, textAlign: 'center', backgroundColor: 'grey.200' }}>
        <Typography variant="body2">CMP9134 Software Engineering • University of Lincoln • 2025</Typography>
      </Box>
    </>
  );
};

export default Home;
