import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Container,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  CircularProgress,
  Fade,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia, getRecentSearches, saveSearch } from '../services/openverseApi';
import MediaGrid from '../components/MediaGrid';
import MediaCard from '../components/MediaCard';

const Home = ({ user }) => {
  const [query, setQuery] = useState('');
  const [license, setLicense] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [results, setResults] = useState([]);
  const [recent, setRecent] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  useEffect(() => {
    if (user) {
      (async () => {
        try {
          const recs = await getRecentSearches();
          setRecent(recs.slice(0, 5));
        } catch (err) {
          console.error(err);
        }
      })();
    }
  }, [user]);

  const performSearch = async (term) => {
    const q = term || query;
    if (!q.trim()) return;

    setLoading(true);
    setSearched(true);

    try {
      const data = await searchMedia(q, { license, mediaType });
      setResults(data.results || []);

      if (user) {
        await saveSearch(q);
        setRecent(prev => [q, ...prev.filter(item => item !== q)].slice(0, 5));
      }
    } catch (error) {
      console.error(error);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    performSearch();
  };

  return (
    <>
      <ThreeBackground style={{ zIndex: -1, opacity: 0.6 }} />

      <Box sx={{ pt: 10, pb: 15, textAlign: 'center', position: 'relative' }}>
        <Container maxWidth="md">
          <Fade in timeout={1000}>
            <Typography
              variant="h2"
              component="h1"
              sx={{ fontWeight: 700, mb: 3, color: 'white', textShadow: '0 2px 12px rgba(0,0,0,0.1)' }}
            >
              Openverse Media Explorer
            </Typography>
          </Fade>

          <Fade in timeout={1200}>
            <Typography
              variant="h5"
              sx={{ mb: 5, fontWeight: 400, color: 'rgba(255,255,255,0.9)', maxWidth: '700px', mx: 'auto' }}
            >
              Search and browse open-license images, vectors, and audio via the Openverse API.
            </Typography>
          </Fade>

          <Fade in timeout={1400}>
            <Box
              component="form"
              onSubmit={handleSubmit}
              sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, maxWidth: '800px', mx: 'auto' }}
            >
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Search for media..."
                value={query}
                onChange={e => setQuery(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon color="primary" />
                    </InputAdornment>
                  ),
                  sx: { backgroundColor: 'white', borderRadius: '12px', height: '56px', '& fieldset': { border: 'none' } }
                }}
              />

              <FormControl variant="outlined" sx={{ minWidth: 140 }}>
                <InputLabel id="license-label">License</InputLabel>
                <Select
                  labelId="license-label"
                  value={license}
                  onChange={e => setLicense(e.target.value)}
                  label="License"
                >
                  <MenuItem value="">All</MenuItem>
                  <MenuItem value="cc-by">CC BY</MenuItem>
                  <MenuItem value="cc0">CC0</MenuItem>
                  <MenuItem value="public-domain">Public Domain</MenuItem>
                </Select>
              </FormControl>

              <FormControl variant="outlined" sx={{ minWidth: 140 }}>
                <InputLabel id="media-type-label">Media Type</InputLabel>
                <Select
                  labelId="media-type-label"
                  value={mediaType}
                  onChange={e => setMediaType(e.target.value)}
                  label="Media Type"
                >
                  <MenuItem value="">All</MenuItem>
                  <MenuItem value="image">Image</MenuItem>
                  <MenuItem value="audio">Audio</MenuItem>
                  <MenuItem value="video">Video</MenuItem>
                </Select>
              </FormControl>

              <Button type="submit" variant="contained" disabled={loading} sx={{ px: 4, height: '56px' }}>
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Search'}
              </Button>
            </Box>
          </Fade>

          {user && recent.length > 0 && (
            <Fade in timeout={1600}>
              <Box sx={{ mt: 4, display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: 1 }}>
                <Typography variant="body2" sx={{ mr: 2, color: 'rgba(255,255,255,0.8)' }}>
                  Recent:
                </Typography>
                {recent.map(term => (
                  <Chip
                    key={term}
                    label={term}
                    onClick={() => performSearch(term)}
                    clickable
                    size="small"
                  />
                ))}
              </Box>
            </Fade>
          )}
        </Container>
      </Box>

      <Container sx={{ py: 8 }}>
        {searched && (
          <Box sx={{ mb: 3 }}>
            <Typography variant="h4" sx={{ fontWeight: 600 }}>
              {loading ? 'Searching...' : results.length ? 'Search Results' : 'No Results'}
            </Typography>
            <Typography variant="body1" color="text.secondary">
              {loading
                ? 'Searching Openverse…'
                : results.length
                  ? `Found ${results.length} items for "${query}"`
                  : `No items found for "${query}".`}
            </Typography>
          </Box>
        )}

        {searched && !loading ? (
          <Grid container spacing={3}>
            {results.map(item => (
              <Grid item key={item.id} xs={12} sm={6} md={4}>
                <MediaCard item={item} />
              </Grid>
            ))}
          </Grid>
        ) : (
          !searched && <MediaGrid />
        )}
      </Container>
    </>
  );
};

export default Home;
