// src/theme.js
import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const basePalette = {
  primary: {
    main: 'var(--color-primary)',        // from index.css
    light: 'var(--color-primary-light)',
    dark:  'var(--color-primary-dark)',
    contrastText: '#fff',
  },
  secondary: {
    main: 'var(--color-secondary)',
    light: 'var(--color-secondary-light)',
    dark:  'var(--color-secondary-dark)',
    contrastText: '#fff',
  },
  background: {
    default: 'var(--color-bg)',
    paper:   'var(--color-bg-paper)',
  },
  text: {
    primary:   'var(--color-text)',
    secondary: 'var(--color-text-secondary)',
  },
  error:   { main: '#ff6b6b' },
  warning: { main: '#ffbe40' },
  info:    { main: '#64c4ff' },
  success: { main: '#67d4a8' },
};

// Dark‐mode overrides
const darkOverrides = {
  palette: {
    background: { default: '#121212', paper: '#1d1d1d' },
    text:       { primary: '#eee', secondary: '#bbb' },
  },
};

export default function getAppTheme(mode = 'light') {
  let theme = createTheme({
    palette: {
      mode,
      ...basePalette,
      ...(mode === 'dark' ? darkOverrides.palette : {}),
    },
    typography: {
      fontFamily: "'Poppins', 'Roboto', 'Helvetica', 'Arial', sans-serif",
      button: {
        fontWeight: 500,
        textTransform: 'none',
      },
      h1: { fontWeight: 600 },
      h2: { fontWeight: 600 },
      h3: { fontWeight: 600 },
      h4: { fontWeight: 600 },
      h5: { fontWeight: 600 },
      h6: { fontWeight: 600 },
    },
    shape: { borderRadius: 12 },
    components: {
      MuiCssBaseline: {
        styleOverrides: {
          body: {
            backgroundColor: 'var(--color-bg)',
            transition: 'background-color 0.3s ease',
          },
        },
      },
      MuiButton: {
        styleOverrides: {
          root: {
            borderRadius: 8,
            padding: '8px 16px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            transition: 'all 0.2s ease-in-out',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 8px 16px rgba(0,0,0,0.15)',
            },
          },
          containedPrimary: {
            background: 'linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-dark) 100%)',
          },
          containedSecondary: {
            background: 'linear-gradient(135deg, var(--color-secondary) 0%, var(--color-secondary-dark) 100%)',
          },
        },
      },
      MuiLink: {
        styleOverrides: {
          root: {
            color: 'var(--color-primary)',
            textDecoration: 'none',
            '&:hover': {
              textDecoration: 'underline',
            },
          },
        },
      },
      MuiPaper: {
        styleOverrides: {
          root: {
            backgroundColor: mode === 'dark' ? '#1d1d1d' : '#fff',
          },
        },
      },
      MuiCard: {
        styleOverrides: {
          root: {
            borderRadius: 16,
            boxShadow: '0px 8px 24px rgba(0,0,0,0.08)',
          },
        },
      },
      MuiFormHelperText: {
        styleOverrides: {
          root: {
            color: 'var(--color-text-secondary)',
          },
        },
      },
      MuiTextField: {
        defaultProps: {
          variant: 'outlined',
        },
        styleOverrides: {
          root: {
            '& .MuiOutlinedInput-root': {
              borderRadius: 8,
            },
          },
        },
      },
      MuiAppBar: {
        styleOverrides: {
          root: {
            backgroundColor: mode === 'dark' ? '#1d1d1d' : '#fff',
            color: mode === 'dark' ? '#fff' : '#323259',
            boxShadow: '0px 2px 8px rgba(0,0,0,0.1)',
          },
        },
      },
    },
  });

  // Responsive typography
  theme = responsiveFontSizes(theme);

  return theme;
}
