import { useState, useEffect } from 'react';
import { searchMedia, saveSearch, getRecentSearches, deleteSearch } from '../services/api';

const Home = ({ user }) => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [recentSearches, setRecentSearches] = useState([]);

    useEffect(() => {
        if (user) {
            getRecentSearches().then(res => setRecentSearches(res.data));
        }
    }, [user]);

    const handleSearch = async () => {
        const res = await searchMedia(query);
        setResults(res.data.results);
        if (user) await saveSearch(query);
    };

    const handleDeleteSearch = async (id) => {
        await deleteSearch(id);
        setRecentSearches(recentSearches.filter(search => search.id !== id));
    };

    return (
        <div>
            <input type="text" value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Search media..." />
            <button onClick={handleSearch}>Search</button>

            <h3>Results:</h3>
            <div>
                {results.map(media => (
                    <img key={media.id} src={media.thumbnail} alt={media.title} width="100" />
                ))}
            </div>

            {user && (
                <>
                    <h3>Recent Searches:</h3>
                    <ul>
                        {recentSearches.map(search => (
                            <li key={search.id}>
                                {search.query} <button onClick={() => handleDeleteSearch(search.id)}>X</button>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default Home;
