import React, { useState, useMemo } from 'react';
import {
  createTheme,
  ThemeProvider,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Box,
  TextField,
  Button,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActionArea,
  InputAdornment,
  Chip,
  Switch,
  Skeleton
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ThreeBackground from '../components/ThreeBackground';
import { searchMedia } from '../services/openverseApi';

const trendingTopics = ['Photography', 'Art', 'Nature', 'Technology', 'Architecture'];

const Home = ({ user }) => {
  // Theme state
  const [darkMode, setDarkMode] = useState(false);
  const theme = useMemo(
    () =>
      createTheme({
        palette: { mode: darkMode ? 'dark' : 'light', primary: { main: '#1976d2' } }
      }),
    [darkMode]
  );

  // Search and UI states
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);
  const [error, setError] = useState('');

  const runSearch = async (term) => {
    const q = term || query;
    if (!q.trim()) {
      setError('Please enter a search term.');
      return;
    }
    if (q.trim().length < 3) {
      setError('Please enter at least 3 characters.');
      return;
    }
    setError('');
    setLoading(true);
    setSearched(true);
    try {
      const data = await searchMedia(q);
      setResults(data.results || []);
    } catch (err) {
      console.error('Search error', err);
      setError('Network error, please try again.');
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    runSearch();
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ThreeBackground style={{ position: 'fixed', inset: 0, zIndex: -1, opacity: 0.1 }} />

      {/* Header */}
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h5" sx={{ flexGrow: 1 }}>
            LincolnVerse
          </Typography>
          <Switch
            checked={darkMode}
            onChange={() => setDarkMode(!darkMode)}
            inputProps={{ 'aria-label': 'Toggle dark mode' }}
          />
          {user && (
            <Typography sx={{ ml: 2 }} aria-label={`User ${user.username}`}>
              Hi, {user.username}
            </Typography>
          )}
        </Toolbar>
      </AppBar>

      {/* Hero & Search */}
      <Box
        sx={{
          pt: 4,
          pb: 6,
          textAlign: 'center',
          backgroundColor: darkMode ? '#121212' : '#e3f2fd'
        }}
      >
        <Container maxWidth="sm">
          <Typography variant="h3" gutterBottom>
            Discover Media
          </Typography>
          <Typography variant="subtitle1" gutterBottom>
            Search open-license images, audio, and video.
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', mt: 2 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search for media..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              inputProps={{ 'aria-label': 'Search media' }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                )
              }}
            />
            <Button
              type="submit"
              variant="contained"
              sx={{ ml: 1 }}
              disabled={loading}
              aria-label="Submit search"
            >
              {loading ? <CircularProgress size={24} /> : 'Search'}
            </Button>
          </Box>

          {!searched && (
            <Box sx={{ mt: 4 }}>
              {trendingTopics.map((topic) => (
                <Chip
                  key={topic}
                  label={topic}
                  onClick={() => runSearch(topic)}
                  clickable
                  aria-label={`Trending topic ${topic}`}
                  sx={{ m: 0.5 }}
                />
              ))}
            </Box>
          )}
        </Container>
      </Box>

      {/* Results and Feedback */}
      <Container sx={{ py: 4 }}>
        {error && (
          <Typography variant="body1" color="error" sx={{ mb: 2 }}>
            {error}
          </Typography>
        )}
        {searched && !error && (
          <Typography variant="h5" gutterBottom>
            {loading
              ? 'Searching...'
              : results.length
                ? `Results for "${query}"`
                : 'No results found'}
          </Typography>
        )}

        {loading ? (
          <Grid container spacing={2}>
            {Array.from({ length: 6 }).map((_, idx) => (
              <Grid item xs={12} sm={6} md={4} key={idx}>
                <Skeleton variant="rectangular" height={160} />
                <Skeleton width="60%" sx={{ mt: 1 }} />
                <Skeleton width="40%" />
              </Grid>
            ))}
          </Grid>
        ) : (
          <Grid container spacing={2}>
            {results.map((item) => (
              <Grid item xs={12} sm={6} md={4} key={item.id}>
                <Card>
                  <CardActionArea
                    component="a"
                    href={item.url}
                    target="_blank"
                    aria-label={`View media ${item.title || 'Untitled'}`}
                  >
                    <CardMedia
                      component="img"
                      height="160"
                      image={item.thumbnail || item.url}
                      alt={item.title || 'Media item'}
                      loading="lazy"
                    />
                    <CardContent>
                      <Typography
                        variant="subtitle1"
                        sx={{ fontWeight: 600 }}
                      >
                        {item.title || 'Untitled'}
                      </Typography>
                      <Typography
                        variant="body2"
                        color="text.secondary"
                      >
                        {item.creator_display_name || 'Unknown'}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>

      {/* Footer */}
      <Box
        sx={{
          py: 2,
          textAlign: 'center',
          backgroundColor: darkMode ? '#1d1d1d' : '#e3f2fd'
        }}
      >
        <Typography variant="body2">
          LincolnVerse • Noel Adesola • 2025
        </Typography>
      </Box>
    </ThemeProvider>
  );
};

export default Home;
